class Solution:
    def findTheCity_wrong(self, n: int, edges: List[List[int]], distanceThreshold: int) -> int:
        # what å›æº¯æ·±æœï¼Œæ€è·¯æ­£ç¡®ä½†è¶…æ—¶ï¼Œ
        graph = [[] for _ in range(n)]
        for edge in edges:
            graph[edge[0]].append((edge[1], edge[2]))
            graph[edge[1]].append((edge[0], edge[2]))

        # æ”¹ç”¨é›†åˆå­˜å‚¨å¯è¾¾åŸå¸‚ï¼Œè‡ªåŠ¨å»é‡
        reachable = {i: set() for i in range(n)}

        # iè®°å½•å½“å‰åŸå¸‚ï¼Œnodeè®°å½•å½“å‰åŸå¸‚è®¿é—®çš„åŸå¸‚ï¼Œvisitedè®°å½•å·²è®¿é—®çš„åŸå¸‚ï¼Œ
        # curWeightæ˜¯è¿˜å‰©ä¸‹çš„å¯ç”¨æƒå€¼
        # å¦‚æœæƒå€¼ä¸å¤Ÿï¼Œè¯´æ˜ä¸åœ¨ç»§ç»­äº†ï¼Œ
        # å¦‚æœå½“å‰è®¿é—®çš„åŸå¸‚ä¸æ˜¯è‡ªå·±ï¼Œåˆ™åŠ å…¥åŸå¸‚
        def dfs(i, node, visited, curWeight):
            if curWeight < 0:
                return
            # åˆ°è¿™ä¸€æ­¥è¯´æ˜è¿˜æœ‰å‰©çš„æƒå€¼ï¼Œä¸”å½“å‰è®¿é—®çš„åŸå¸‚ä¸æ˜¯è‡ªå·±
            if node != i:  # ä¸ç»Ÿè®¡è‡ªå·±
                reachable[i].add(node)
            #     åšæ·±æœ
            for nei, weight in graph[node]:
                if nei not in visited:
                    visited.add(nei)
                    dfs(i, nei, visited, curWeight - weight)
                    visited.remove(nei)  # å›æº¯

        for i in range(n):
            # æ²¡è½®éƒ½å¾—æœ‰ä¸ªvisitedé›†åˆï¼Œä¸èƒ½ä¸€ä¸ªvisitedå˜é‡
            visited = set()
            visited.add(i)
            dfs(i, i, visited, distanceThreshold)
        print(reachable)
        # ç»Ÿè®¡æ¯ä¸ªåŸå¸‚å¯è¾¾çš„åŸå¸‚æ•°é‡
        dic = {city: len(cities) for city, cities in reachable.items()}
        min_reachable = min(dic.values())
        candidates = [city for city in dic if dic[city] == min_reachable]
        return max(candidates)

    def findTheCity1(self, n: int, edges: List[List[int]], distanceThreshold: int) -> int:
        # note å»ºç«‹ä¸´æ¥è¡¨ï¼Œå¯¹äºè¿™ç§æœ‰æƒå€¼çš„ï¼Œä¸´æ¥çŸ©é˜µå¥½ä¸€ç‚¹,å¯åˆ°çš„æ˜¯æƒå€¼ï¼Œä¸åˆ°çš„æ˜¯inf
        #  å¯¹äºæ— æƒå€¼çš„ï¼Œå¯ä»¥ç”¨é‚»æ¥è¡¨
        # flody-warshallç®—æ³•ï¼Œæ—¶é—´å¤æ‚åº¦O(n^3),ç”¨ä¸´æ¥çŸ©é˜µ
        graph = [[float('inf')] * n for _ in range(n)]
        for i in range(n):
            graph[i][i] = 0
        for u, v, w in edges:
            graph[u][v] = w
            graph[v][u] = w
        # è®¡ç®—æ‰€æœ‰èŠ‚ç‚¹å¯¹çš„æœ€çŸ­è·¯å¾„
        # floyd-warshall: æ—¶é—´å¤æ‚åº¦O(n^3)
        # ä»‹ç»ä¸€ä¸‹floyd-warshallç®—æ³•ï¼Œè¿™ä¸ªç®—æ³•æ˜¯æ±‚æ‰€æœ‰èŠ‚ç‚¹å¯¹çš„æœ€çŸ­è·¯å¾„ï¼Œ
        # ç®—æ³•çš„åŸºæœ¬æ€æƒ³æ˜¯åŠ¨æ€è§„åˆ’ï¼Œå³å…ˆå¯¹ä»»æ„ä¸¤ç‚¹iå’Œjï¼Œè®¡ç®—dp[i][j]ï¼Œ
        # ç„¶åå†å¯¹ä»»æ„ä¸¤ç‚¹iå’Œjï¼Œè®¡ç®—dp[i][k]+dp[k][j]ï¼Œ
        # è¿™æ ·å°±èƒ½æ±‚å‡ºä»»æ„ä¸¤ç‚¹iå’Œjçš„æœ€çŸ­è·¯å¾„ã€‚
        # ç®—æ³•çš„å…·ä½“æ­¥éª¤å¦‚ä¸‹ï¼š
        # 1. åˆå§‹åŒ–dpæ•°ç»„ï¼Œä»¤dp[i][i]=0ï¼Œdp[i][j]=w(i,j)ï¼Œå…¶ä¸­w(i,j)æ˜¯è¾¹(i,j)çš„æƒå€¼ã€‚
        # 2. å¯¹äºä»»æ„k!=i,jï¼Œä»¤dp[i][k]=min(dp[i][k],dp[i][j]+dp[j][k])ï¼Œ
        # å³å¦‚æœç»è¿‡èŠ‚ç‚¹jåˆ°è¾¾èŠ‚ç‚¹kçš„æœ€çŸ­è·¯å¾„æ¯”ç»è¿‡èŠ‚ç‚¹iåˆ°è¾¾èŠ‚ç‚¹kçš„æœ€çŸ­è·¯å¾„çŸ­ï¼Œ
        # åˆ™æ›´æ–°dp[i][k]çš„å€¼ã€‚
        # 3. é‡å¤æ­¥éª¤2ï¼Œç›´åˆ°dp[i][j]çš„å€¼ä¸å†å˜åŒ–ã€‚
        for k in range(n):
            for i in range(n):
                for j in range(n):
                    # å¦‚æœé€šè¿‡kåˆ°è¾¾içš„è·ç¦»æ¯”é€šè¿‡jåˆ°è¾¾içš„è·ç¦»çŸ­ï¼Œåˆ™æ›´æ–°iåˆ°jçš„è·ç¦»
                    if graph[i][k] + graph[k][j] < graph[i][j]:
                        graph[i][j] = graph[i][k] + graph[k][j]
        for i in range(n):
            print(graph[i])
        # å½“å‰å·²ç»å¾—åˆ°æ¯ä¸ªèŠ‚ç‚¹åˆ°å…¶ä»–èŠ‚ç‚¹éœ€è¦èŠ±è´¹çš„è·ç¦»
        res = {i: 0 for i in range(n)}
        for i in range(n):
            for j in range(n):
                if i != j and graph[i][j] <= distanceThreshold:
                    res[i] += 1
        print(res)
        # æ‰¾å‡ºè·ç¦»é˜ˆå€¼å†…å¯åˆ°è¾¾çš„åŸå¸‚æ•°é‡æœ€å°‘çš„åŸå¸‚
        min_reachable = min(res.values())
        candidates = [city for city in res if res[city] == min_reachable]
        print(candidates)
        return max(candidates)

    def findTheCity(self, n: int, edges: List[List[int]], distanceThreshold: int) -> int:
        # SPFAï¼ˆShortest Path Faster Algorithm)
        # æ€è·¯ï¼š
        # 1. å»ºç«‹é‚»æ¥è¡¨
        # 3. å¯¹äºæ¯ä¸ªèŠ‚ç‚¹ï¼Œåˆå§‹åŒ–ä¸€ä¸ªé˜Ÿåˆ—ï¼Œå°†å…¶åŠ å…¥é˜Ÿåˆ—ä¸­
        # 4. å¯¹äºé˜Ÿåˆ—ä¸­çš„æ¯ä¸ªèŠ‚ç‚¹ï¼Œéå†å…¶é‚»å±…ï¼Œå¦‚æœé‚»å±…çš„è·ç¦»å°äºç­‰äºé˜ˆå€¼ï¼Œåˆ™å°†é‚»å±…åŠ å…¥é˜Ÿåˆ—
        # 5. é‡å¤æ­¥éª¤4ï¼Œç›´åˆ°é˜Ÿåˆ—ä¸ºç©º

        # Step 1: æ„å»ºå›¾çš„é‚»æ¥è¡¨è¡¨ç¤º
        # graph[u] å­˜å‚¨çš„æ˜¯ä¸èŠ‚ç‚¹ u ç›´æ¥ç›¸è¿çš„èŠ‚ç‚¹åŠå…¶è¾¹çš„æƒé‡
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))  # æ— å‘å›¾ï¼Œéœ€è¦åŒå‘æ·»åŠ 
            graph[v].append((u, w))

        min_cities = n  # åˆå§‹åŒ–æœ€å°å¯è¾¾åŸå¸‚æ•°ä¸ºæœ€å¤§å€¼ï¼ˆnï¼‰
        result = 0  # åˆå§‹åŒ–ç»“æœä¸ºç¼–å·æœ€å°çš„åŸå¸‚ï¼ˆ0ï¼‰

        # Step 2: å¯¹æ¯ä¸ªèŠ‚ç‚¹è¿è¡Œ SPFA ç®—æ³•
        '''
        éå†æ¯ä¸ªèŠ‚ç‚¹ï¼Œå¯¹å…¶è¿›è¡Œ SPFA ç®—æ³•ï¼Œç®—æ³•çš„åŸºæœ¬æ€æƒ³æ˜¯ï¼š
        ç»´æŠ¤ä¸€ä¸ªé˜Ÿåˆ—ï¼Œå°†èµ·å§‹èŠ‚ç‚¹åŠ å…¥é˜Ÿåˆ—ï¼Œç„¶åå¼€å§‹éå†é˜Ÿåˆ—ï¼Œå¯¹äºé˜Ÿåˆ—ä¸­çš„æ¯ä¸ªèŠ‚ç‚¹ï¼Œ
        éå†å…¶é‚»å±…ï¼Œå¦‚æœé‚»å±…çš„è·ç¦»å°äºç­‰äºé˜ˆå€¼ï¼Œåˆ™å°†é‚»å±…åŠ å…¥é˜Ÿåˆ—ï¼Œç›´åˆ°é˜Ÿåˆ—ä¸ºç©ºã€‚
        éœ€è¦ä¸€ä¸ªdistè®°å½•æ¯ä¸ªèŠ‚ç‚¹çš„æœ€çŸ­è·ç¦»ï¼Œä¸€ä¸ªqueueè®°å½•å½“å‰å¾…éå†çš„èŠ‚ç‚¹ã€‚
        ä¸€ä¸ªin_queueæ•°ç»„è®°å½•æ¯ä¸ªèŠ‚ç‚¹æ˜¯å¦åœ¨é˜Ÿåˆ—ä¸­ï¼Œé¿å…é‡å¤å…¥é˜Ÿ
        '''
        for i in range(n):
            # SPFA åˆå§‹åŒ–
            dist = [float('inf')] * n  # åˆå§‹åŒ–è·ç¦»æ•°ç»„ï¼Œæ‰€æœ‰è·ç¦»åˆå§‹ä¸ºæ— ç©·å¤§
            dist[i] = 0  # æºèŠ‚ç‚¹åˆ°è‡ªèº«çš„è·ç¦»ä¸º 0
            queue = deque([i])  # åˆå§‹åŒ–é˜Ÿåˆ—ï¼ŒåŠ å…¥æºèŠ‚ç‚¹
            in_queue = [False] * n  # æ ‡è®°èŠ‚ç‚¹æ˜¯å¦åœ¨é˜Ÿåˆ—ä¸­
            in_queue[i] = True  # æºèŠ‚ç‚¹åœ¨é˜Ÿåˆ—ä¸­

            # SPFA ä¸»å¾ªç¯
            while queue:
                u = queue.popleft()  # å–å‡ºé˜Ÿåˆ—ä¸­çš„ç¬¬ä¸€ä¸ªèŠ‚ç‚¹
                in_queue[u] = False  # æ ‡è®°è¯¥èŠ‚ç‚¹å·²ä¸åœ¨é˜Ÿåˆ—ä¸­

                # éå†èŠ‚ç‚¹ u çš„æ‰€æœ‰é‚»å±…èŠ‚ç‚¹
                for v, w in graph[u]:
                    # æ¾å¼›æ“ä½œï¼šå¦‚æœé€šè¿‡ u åˆ° v çš„è·¯å¾„æ¯”å½“å‰å·²çŸ¥çš„æ›´çŸ­ï¼Œåˆ™æ›´æ–°
                    if dist[v] > dist[u] + w:
                        dist[v] = dist[u] + w  # æ›´æ–°è·ç¦»
                        # å¦‚æœ v ä¸åœ¨é˜Ÿåˆ—ä¸­ï¼Œåˆ™åŠ å…¥é˜Ÿåˆ—,é¿å…é‡å¤å…¥é˜Ÿ
                        if not in_queue[v]:
                            queue.append(v)
                            in_queue[v] = True

            # Step 3: ç»Ÿè®¡åœ¨é˜ˆå€¼è·ç¦»å†…çš„å¯è¾¾åŸå¸‚æ•°é‡
            count = 0
            for d in dist:
                if 0 < d <= distanceThreshold:  # æ’é™¤è‡ªèº«ï¼Œä¸”è·ç¦»åœ¨é˜ˆå€¼å†…
                    count += 1

            # Step 4: æ›´æ–°æœ€ä¼˜ç»“æœ
            if count < min_cities:
                min_cities = count  # æ›´æ–°æœ€å°å¯è¾¾åŸå¸‚æ•°
                result = i  # æ›´æ–°å½“å‰æœ€ä¼˜åŸå¸‚
            elif count == min_cities:
                if i > result:  # å¦‚æœå¯è¾¾åŸå¸‚æ•°ç›¸åŒï¼Œé€‰æ‹©ç¼–å·æ›´å¤§çš„åŸå¸‚
                    result = i

        return result

    def findTheCit3(self, n: int, edges: List[List[int]], distanceThreshold: int) -> int:
        # dijkstraç®—æ³•ï¼Œæ—¶é—´å¤æ‚åº¦O(n^2)
        # ä»‹ç»ä¸€ä¸‹dijkstraç®—æ³•ï¼Œè¿™ä¸ªç®—æ³•æ˜¯æ±‚å•æºæœ€çŸ­è·¯å¾„ï¼Œ
        # ç®—æ³•çš„åŸºæœ¬æ€æƒ³æ˜¯ç»´æŠ¤ä¸€ä¸ªä¼˜å…ˆé˜Ÿåˆ—ï¼Œå°†èµ·å§‹èŠ‚ç‚¹åŠ å…¥é˜Ÿåˆ—ï¼Œç„¶åå¼€å§‹éå†é˜Ÿåˆ—ï¼Œ
        # å¯¹äºé˜Ÿåˆ—ä¸­çš„æ¯ä¸ªèŠ‚ç‚¹ï¼Œéå†å…¶é‚»å±…ï¼Œå¦‚æœé‚»å±…çš„è·ç¦»å°äºç­‰äºå½“å‰èŠ‚ç‚¹çš„è·ç¦»ï¼Œ
        # Step 1: æ„å»ºé‚»æ¥è¡¨è¡¨ç¤ºçš„å›¾
        # graph[u] å­˜å‚¨çš„æ˜¯ä¸èŠ‚ç‚¹ u ç›´æ¥ç›¸è¿çš„èŠ‚ç‚¹åŠå…¶è¾¹çš„æƒé‡
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))  # æ— å‘å›¾ï¼Œéœ€è¦åŒå‘æ·»åŠ 
            graph[v].append((u, w))

        min_cities = n  # åˆå§‹åŒ–æœ€å°å¯è¾¾åŸå¸‚æ•°ä¸ºæœ€å¤§å€¼ï¼ˆnï¼‰
        result = 0  # åˆå§‹åŒ–ç»“æœä¸ºç¼–å·æœ€å°çš„åŸå¸‚ï¼ˆ0ï¼‰

        # Step 2: å¯¹æ¯ä¸ªèŠ‚ç‚¹è¿è¡Œ Dijkstra ç®—æ³•
        for i in range(n):
            # Dijkstra åˆå§‹åŒ–
            dist = [float('inf')] * n  # åˆå§‹åŒ–è·ç¦»æ•°ç»„ï¼Œæ‰€æœ‰è·ç¦»åˆå§‹ä¸ºæ— ç©·å¤§
            dist[i] = 0  # æºèŠ‚ç‚¹åˆ°è‡ªèº«çš„è·ç¦»ä¸º 0
            heap = [(0, i)]  # ä½¿ç”¨ä¼˜å…ˆé˜Ÿåˆ—ï¼ˆæœ€å°å †ï¼‰ï¼Œå­˜å‚¨ (è·ç¦», èŠ‚ç‚¹)

            while heap:
                current_dist, u = heapq.heappop(heap)  # å–å‡ºå½“å‰è·ç¦»æœ€å°çš„èŠ‚ç‚¹
                if current_dist > dist[u]:
                    continue  # å¦‚æœå½“å‰è·ç¦»ä¸æ˜¯æœ€çŸ­çš„ï¼Œè·³è¿‡ï¼ˆé¿å…é‡å¤å¤„ç†ï¼‰

                # éå†èŠ‚ç‚¹ u çš„æ‰€æœ‰é‚»å±…èŠ‚ç‚¹
                for v, w in graph[u]:
                    if dist[v] > dist[u] + w:  # æ¾å¼›æ“ä½œ
                        dist[v] = dist[u] + w  # æ›´æ–°è·ç¦»
                        heapq.heappush(heap, (dist[v], v))  # å°†æ–°è·ç¦»åŠ å…¥å †

            # Step 3: ç»Ÿè®¡åœ¨é˜ˆå€¼è·ç¦»å†…çš„å¯è¾¾åŸå¸‚æ•°é‡
            count = sum(1 for d in dist if 0 < d <= distanceThreshold)

            # Step 4: æ›´æ–°æœ€ä¼˜ç»“æœ
            if count < min_cities:
                min_cities = count  # æ›´æ–°æœ€å°å¯è¾¾åŸå¸‚æ•°
                result = i  # æ›´æ–°å½“å‰æœ€ä¼˜åŸå¸‚
            elif count == min_cities:
                if i > result:  # å¦‚æœå¯è¾¾åŸå¸‚æ•°ç›¸åŒï¼Œé€‰æ‹©ç¼–å·æ›´å¤§çš„åŸå¸‚
                    result = i

        return result

